mapper.select:
SELECT MEMBER_NAME, MEMBER_EMAIL
FROM "MEMBER"
WHERE MEMBER_NAME = #{memberName}
AND MEMBER_TEL = #{memberTel}
AND MEMBER_DEL_FL = 'N'

mapper.update:
UPDATE MEMBER SET 
 	MEMBER_NICKNAME = #{memberNickname} ,
 	MEMBER_TEL = #{memberTel} ,
 	MEMBER_ADDRESS = #{memberAddress} 
 WHERE MEMBER_NO = #{memberNo}
